INSERT INTO `newspapers` VALUES (18, 'ASUS VivoBook 14/15 ra mắt, nhỏ gọn, 4 màu sắc', 6, '2019-06-16', NULL, NULL, 0, 1, 0, 'Chiều nay 21/5 tại TP.HCM, ASUS chính thức ra mắt bộ đôi VivoBook 14/15 (A412/A512) tại sự kiện “Vivobook – Cuộc chơi lớn”. Đây là...', 2, 'doisong', 'laptop');
INSERT INTO `newspapers` VALUES (19, 'Lenovo trưng bày mẫu laptop màn hình gập đầu tiên trên thế giới', 6, '2019-06-14', NULL, NULL, 0, 1, 0, 'Lenovo đang phát triển công nghệ màn hình gập để biến chiếc laptop thông thường thành một thiết bị nhỏ gọn và phù hợp với...', 2, 'doisong', 'laptop');
INSERT INTO `newspapers` VALUES (20, 'Ra mắt loạt laptop ASUS trang bị AMD Ryzen Mobile', 6, '2019-06-14', NULL, NULL, 0, 1, 10, 'ASUS vừa công bố chính thức hợp tác chiến lược cùng AMD trong sự kiện chủ đề “The Choice of MORE”, đồng thời giới thiệu...', 2, 'doisong', 'laptop');
INSERT INTO `newspapers` VALUES (41, 'Cùng các chuyên gia đầu ngành trí tuệ nhân tạo trả lời câu hỏi \"AI là gì?\"', 7, '2019-06-14', NULL, NULL, 0, 1, 0, 'Buổi tọa đàm tổ chức vào 22/5 này sẽ giải đáp tất cả những thắc mắc liên quan đến AI của độc giả.', 2, 'doisong', 'AI');
INSERT INTO `newspapers` VALUES (42, 'Con chip bé nhỏ này sẽ phá bỏ định luật Moore để trở thành kẻ dẫn đầu ngành trí tuệ nhân tạo', 7, '2019-06-14', NULL, NULL, 0, 1, 0, 'Con chip Eyeriss xuất hiện trước những \"tay to mặt lớn\" của ngành công nghệ thế giới, rồi để lại những ấn tượng sâu sắc về tương lai có thể có của ngành trí tuệ nhân tạo.', 2, 'doisong', 'AI');
INSERT INTO `newspapers` VALUES (43, 'Ở Trung Quốc, đi bộ sai luật cũng sẽ bị trí tuệ nhân tạo phát hiện và xử phạt', 7, '2019-06-14', NULL, NULL, 0, 1, 0, 'Từ một công ty vô danh, Intellifusion đã trở thành cánh tay phải đắc lực của cảnh sát và chính quyền thành phố Thâm Quyến (Trung Quốc), góp phần giữ gìn trật tự giao thông và an ninh công cộng.', 2, 'doisong', 'AI');
INSERT INTO `newspapers` VALUES (44, 'Ở Amazon, robot có thể đuổi việc con người nếu thấy hợp lý', 7, '2019-06-14', NULL, NULL, 0, 1, 0, 'Thế giới nơi con người bị theo dõi và giám sát bởi máy móc tưởng chừng như chỉ có trong phim khoa học viễn tưởng, nay đã tồn tại ở Amazon.', 2, 'doisong', 'AI');
INSERT INTO `newspapers` VALUES (45, 'Phần Lan: tù nhân là những người huấn luyện cho AI thông minh hơn', 7, '2019-06-14', NULL, NULL, 0, 1, 10, 'Trong những \"nhà tù mở\" của Phần Lan, có những tù nhân đang ngồi trước màn hình máy tính, nhận công việc dán nhãn dữ liệu nặng nhọc.', 2, 'doisong', 'AI');
INSERT INTO `newspapers` VALUES (51, 'Câu đố triệu đô bất khả thi: máy tính cũng phải mất tới vài ngàn năm mới tìm ra câu trả lời', 8, '2019-06-14', NULL, NULL, 0, 1, 0, 'Thậm chí nếu bạn chứng minh được câu đố này bất khả thi với một cỗ máy, bạn cũng nhận được giải thưởng 1 triệu USD.', 2, 'tritue', 'AI');
INSERT INTO `newspapers` VALUES (52, 'Đây không phải mèo thật, mà chỉ là mèo do AI tạo ra', 8, '2019-06-16', NULL, NULL, 0, 1, 10, 'Trước đó, báo chí cũng đã đưa tin về một website với công cụ AI có thể tạo ra hình ảnh một con người hoàn toàn không tồn tại trên thế giới.', 2, 'tritue', 'AI');
INSERT INTO `newspapers` VALUES (53, '“Cha đẻ” của trí tuệ nhân tạo lo ngại về việc Trung Quốc lạm dụng AI để giám sát người dân', 8, '2019-06-14', NULL, NULL, 0, 1, 0, 'Nếu không có sự giám sát từ nhiều phía, trí tuệ nhân tạo (AI) có thể sẽ trở thành công cụ tiếp tay cho các mục đích xấu, đặc biệt tại Trung Quốc.', 2, 'tritue', 'AI');
INSERT INTO `newspapers` VALUES (54, 'Adobe thêm tính năng \'zoom và thêm chi tiết\' bằng AI cho trình chỉnh sửa ảnh Lightroom CC', 8, '2019-06-14', NULL, NULL, 0, 1, 0, 'Chúng ta đang ngày càng gần công nghệ \'Enhance\' như ma thuật của các bộ phim trinh thám!', 2, 'tritue', 'AI');
INSERT INTO `newspapers` VALUES (55, 'Hoàn thành bản giao hưởng dang dở của Schubert, AI của smartphone đang mở ra một chân trời mới cho nghệ thuật', 8, '2019-06-14', NULL, NULL, 0, 1, 0, 'Nhà soạn nhạc tài năng Schubert đã sáng tác dang dở bản Giao hưởng số 8 nhưng nó vừa được hoàn thiện bởi AI trên một chiếc smartphone.', 2, 'tritue', 'AI');
INSERT INTO `newspapers` VALUES (61, 'Ý kiến cá nhân: Canon không nên chạy theo xu hướng máy ảnh không gương lật', 10, '2019-06-14', NULL, NULL, 0, 1, 0, 'Cuộc cách mạng máy ảnh không gương lật được tạo ra bởi Sony có lẽ không có chỗ trống cho Canon.', 2, 'tritue', 'AI');
INSERT INTO `newspapers` VALUES (62, 'Cầm đèn chạy trước ô tô: Một người Nhật đem bán ống kính Sony khi nó còn chưa được công bố', 9, '2019-06-14', NULL, NULL, 0, 1, 0, 'Đây được cho là nguyên mẫu của một sản phẩm sẽ được hãng công bố trong một vài tuần tới.', 2, 'photo', 'AI');
INSERT INTO `newspapers` VALUES (63, 'Sony vượt mặt Nikon để trở thành hãng máy ảnh thứ 2 Thế giới', 9, '2019-06-14', NULL, NULL, 0, 1, 0, 'Vị trí đứng nhất vẫn đang thuộc về Canon.', 2, 'photo', 'congnghe');
INSERT INTO `newspapers` VALUES (64, 'Canon thay đổi thiết kế hotshoe trên máy ảnh giá rẻ buộc người dùng phải sử dụng flash của hãng: combo cho người tập sự liệu có còn rẻ?', 10, '2019-06-14', NULL, NULL, 0, 1, 0, 'Canon đang chơi trò chơi \'mèo vờn chuột\' với người dùng, tạo ra những điều gây khó chịu ở các sản phẩm tầm thấp để bắt họ phải mua các dòng máy đắt tiền.', 2, 'photo', 'congnghe');
INSERT INTO `newspapers` VALUES (65, 'Trên tay máy ảnh Sony RX0 mark II: Siêu nhỏ, quay phim 4K, màn hình lật', 9, '2019-06-14', NULL, NULL, 0, 1, 0, 'Có quá nhiều công nghệ được \'nhét\' vào thân máy chỉ cầm lọt trong lòng bàn tay.', 2, 'photo', 'congnghe');
INSERT INTO `newspapers` VALUES (66, 'Sony phát triển cảm biến Full-frame 100MP, quay phim 6K, rất có tiềm năng áp dụng thực tế', 9, '2019-06-14', NULL, NULL, 0, 1, 0, 'Câu hỏi ta phải đặt ra đó là: liệu có ai tận dụng được hết độ phân giải cao như vậy?', 2, 'photo', 'congnghe');
INSERT INTO `newspapers` VALUES (67, 'Máy ảnh không gương lật của Canon không dành cho người thuận mắt trái', 10, '2019-06-14', NULL, NULL, 0, 1, 0, 'Hãng có thể mất tới 1/3 số khách hàng vì lỗi trong thiết kế này.', 2, 'photo', 'congnghe');
INSERT INTO `newspapers` VALUES (68, 'Máy ảnh Sony trị giá 2200 USD \'đi tong\' cảm biến vì quay quá trình xóa hình xăm bằng laser', 9, '2019-06-14', NULL, NULL, 0, 1, 0, 'Lại một lần nữa, laser trở thành đối thủ \'không đội trời chung\' của cảm biến máy ảnh.', 2, 'photo', 'congnghe');
INSERT INTO `newspapers` VALUES (69, 'Canon chính thức ra mắt EOS RP: cảm biến full-frame, kích thước nhỏ gọn, giá 38 triệu đồng', 10, '2019-06-14', NULL, NULL, 0, 1, 0, 'Bên cạnh đó Canon cũng giới thiệu dòng máy in phun PIXMA TS707 với nhiều giải pháp in ấn cho gia đình và doanh nghiệp nhỏ.', 2, 'photo', 'congnghe');
INSERT INTO `newspapers` VALUES (70, 'Canon công bố EOS RP: Máy ảnh Full-frame nhỏ và nhẹ nhất Thế giới, giá rẻ hơn EOS R', 10, '2019-06-16', NULL, NULL, 0, 1, 0, 'Đúng như dự đoán, sản phẩm này có cấu hình khá giống với chiếc 6D Mark II đã có mặt trên thị trường.', 2, 'photo', 'congnghe');
INSERT INTO `newspapers` VALUES (81, 'Combo 9 khóa học thực chiến cho Designer chỉ 499k, nâng tầm sự nghiệp ngay sau Tết', 11, '2019-06-14', NULL, NULL, 0, 1, 0, 'Thành thạo 9 ứng dụng nổi tiếng cho Designer ngay sau khi học xong với mức giá ưu đãi 80% ... là những gì FEDU dành tặng bạn trong dịp Tết Nguyên Đán sắp tới đây.', 2, 'thietke', 'congnghe');
INSERT INTO `newspapers` VALUES (82, '\"Phù thủy thiết kế iPhone\" Jony Ive quay sang thiết kế cây thông giáng sinh', 11, '2019-06-14', NULL, NULL, 0, 1, 0, 'Giám đốc thiết kế Apple Jony Ive từng được biết đến với những mẫu thiết kế đột phá và có tầm ảnh hưởng trên iPhone, iPad và Macbook. Thế nhưng có thể nhiều người không biết rằng đôi khi ông vẫn tham gia thực hiện những dự án ngoài lề rất không liên quan.', 2, 'thietke', 'congnghe');
INSERT INTO `newspapers` VALUES (83, 'Dân thiết kế web có cần thiết phải biết code mới có thể thành công?', 11, '2019-06-14', NULL, NULL, 0, 1, 0, 'Một nhà thiết kế web, không nhất thiết phải là chuyên gia về HTML và CSS, nhưng ít nhất phải có hiểu biết cơ bản. Không cần thiết phải code ra một trang web thật chuyên nghiệp, nhưng phải đưa được thiết kế của mình hoạt động được trên trình duyệt (tất nhiên phải giống với bản thiết kế).', 2, 'thietke', 'congnghe');
INSERT INTO `newspapers` VALUES (84, 'Sao năm 2016 rồi mà các trang web \"xấu như ma\" vẫn có nhiều người truy cập thế?', 11, '2019-06-14', NULL, NULL, 0, 1, 0, 'Giữa vô vàn những trang web mới với nhiều tính năng hiện đại mỗi ngày, dường như người dùng những trang web này quan tâm đến nhiều điều khác hơn là một thiết kế đẹp.', 2, 'thietke', 'congnghe');
INSERT INTO `newspapers` VALUES (85, 'Trưởng bộ phận thiết kế của Uber từ chức', 11, '2019-06-14', NULL, NULL, 0, 1, 0, 'Nhiều người cho rằng nguyên nhân đến từ mẫu logo mới gây không ít tranh cãi của hãng này.', 2, 'thietke', 'phone');
INSERT INTO `newspapers` VALUES (91, 'Sử dụng màu sắc trong thiết kế web thế nào cho đúng?', 12, '2019-06-15', NULL, NULL, 0, 1, 0, 'Xu hướng thiết kế phẳng, sử dụng màu khối đang dần khiến các trang web hiện đại ngày càng trở nên khác biệt.', 2, 'thietke', 'phone');
INSERT INTO `newspapers` VALUES (92, 'Bạn có nhận ra sự khác nhau về màu sắc logo Google và Microsoft không?', 12, '2019-06-15', NULL, NULL, 0, 1, 0, 'Những mảng màu tưởng chừng ngẫu hứng nhưng nó vẫn phải tuân theo các quy luật trong thiết kế.', 2, 'thietke', 'phone');
INSERT INTO `newspapers` VALUES (93, '\"Xin lỗi nhé Apple, dân thiết kế chúng tôi không dùng iPad\"', 12, '2019-06-15', NULL, NULL, 0, 1, 0, 'Nhắm vào nhóm khách hàng là các nhà thiết kế đồ họa hay họa sĩ, Apple đang vô cùng tự tin về khả năng đánh chiếm thị trường của iPad Pro. Tuy nhiên, nhiều dân thiết kế chuyên nghiệp lại tỏ ra khá thờ ơ và… phũ phàng.', 2, 'thietke', 'phone');
INSERT INTO `newspapers` VALUES (94, 'Tại sao cuộn chuột liên tục để đọc báo lướt web lại trở nên thịnh hành?', 12, '2019-06-15', NULL, NULL, 0, 1, 0, 'Thay vì điều hướng bằng biểu tượng và đường dẫn, xu thế web hiện đại đang nhấn mạnh vào hình thức cuộn trang.', 2, 'thietke', 'congnghe');
INSERT INTO `newspapers` VALUES (95, 'Thay vì điều hướng bằng biểu tượng và đường dẫn, xu thế web hiện đại đang nhấn mạnh vào hình thức cuộn trang.', 12, '2019-06-15', NULL, NULL, 0, 1, 30, 'Sau khi cố CEO Steve Jobs qua đời, không một ai tại Apple có tầm ảnh hưởng lớn như Jony Ive - Phó chủ tịch mảng thiết kế của hãng này.', 2, 'thietke', 'congnghe');
